import { ArrowLeftOutlined, ArrowRightOutlined} from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container=styled.div`
width: 100%;
height: 100vh;
display: flex;

position:relative;
`
const Wrapper=styled.div`
height: 100%;

`

const Arrow=styled.div`
width: 50px;
height: 50px;
background-color: white;
border-radius: 50%;
display: flex;
align-items: center;
justify-content:center;
position:absolute;
top: 0;
bottom: 0;
left: ${props=>props.direction==="left" && "10px"};
right: ${props=>props.direction==="right" && "10px"};
opacity: 0.5;
margin:auto;
`
const Slide=styled.div`
width: 100vw;
height: 100vh;
display: flex;
align-items: center;`

const ImgContainer=styled.div`
flex:1;
height: 100%;
`
const Image=styled.img`
height: 80%;
`
const InfoContainer=styled.div`
flex:1;
padding: 50px;

`
const Title=styled.h1`
font-size: 70px;`
const Desc=styled.p`
margin: 50px 0px;
font-size: 20px;
font-weight: 200;
letter-spacing:3px;
`
const Button=styled.button`
border:none;
cursor: pointer;
`
const Slider = () => {
  return (
    <Container>
        <Arrow direction="left">
            <ArrowLeftOutlined/>
        </Arrow>
        <Wrapper>
            <Slide>

            <ImgContainer>
            <Image src='https://images.unsplash.com/photo-1589465885857-44edb59bbff2?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=987&q=80'/>
            </ImgContainer>
            <InfoContainer>
                <Title>SUMMER SALE</Title>
                <Desc>ITS TIME TO BE TRENDY, FLAT 30% OFF ON NEW ARRIVALS </Desc>
                <Button>SHOP NOW</Button>
            </InfoContainer>
            </Slide>
        </Wrapper>
        <Arrow direction="right">
            <ArrowRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
